package week1.day2;

public class BitWiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a= 10;
		int b = 22;
		int c = 0 ;
		
		c= a&b;
		System.out.println("a & b :" + c);
		
		c= a|b;
		System.out.println("a & b :" + c);
		
		c= a^b;
		System.out.println("a & b :" + c); 
		
		c= ~ a;
		System.out.println("~ a :" + c);
		
		
		
	}

}
